mapper.select:
select ort.rn , ort.no , ort.title , ort.content , ort.hit , ort.regDate , ort.userNo , ort.name from (select rownum rn , ot.no , ot.title , ot.content , ot.hit , ot.reg_date regDate , ot.user_No userNo , ot.name from (select b.no , b.title , b.content , b.hit , b.reg_date , b.user_no , u.name from board b, users u where b.user_no = u.no
and b.title like '%'|| #{keyword} ||'%'
order by no desc) ot) ort where rn >= #{startRnum} and rn <= #{endRnum}

mapper.update:

update board 
set title = #{title}
  , content = #{content}
  , reg_date = sysDate 
where no = #{no}
